, 'code_generator_lite_edit_generator', 'code_generator_lite_edit_generator');	add_submenu_page('code_generator_lite_generator', 'Generator Style', '', 8, 'code_generator_lite_style', 'code_generator_lite_style');	add_submenu_page('code_generator_lite_generator', 'Edit Code', '', 8, 'code_generator_lite_edit_code', 'code_generator_lite_edit_code');	$code_generator_installed_ver = get_option("code_generator_db_version");		$table_name = $wpdb->prefix . "code_generator_lite";	if($code_generator_lite_installed_ver != $code_generator_lite_db_version) {		$sql = "CREATE TABLE $table_name (		id SMALLINT UNSIGNED NOT NULL PRIMARY KEY, 		name VARCHAR(40) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL, 		value VARCHAR(80) CHARACTER SET utf8 COLLATE utf8_unicode_ci		);";				require_once(ABSPATH . 'wp-admin/includes/upgrade.php');		dbDelta($sql);		add_option("code_generator_lite_db_version", $code_generator_lite_db_version);	}}function code_generator_lite_update_db_check() {    global $code_generator_lite_db_version;		if (get_site_option('code_generator_lite_db_version') != $code_generator_lite_db_version) {        code_generator_lite_setup();    }}add_action('code_generator_lite_loaded', 'code_generator_lite_update_db_check');register_activation_hook(__FILE__, 'code_generator_lite_code_active');add_action('code_generator_lite_code_active_change', 'code_generator_lite_code_active_zero');function code_generator_lite_code_active() {	wp_schedule_event(time(), 'hourly', 'code_generator_lite_code_active_change');}function code_generator_lite_code_active_zero() {	global $wpdb;		$wpdb->query("UPDATE ".$wpdb->prefix."code_generator_lite SET value = case id when '4' then '0' when '10' then '0' when '16' then '0' when '22' then '0' when '28' then '0' when '34' then '0' when '40' then '0' when '46' then '0' when '52' then '0' when '58' then '0' end WHERE id IN ('4','10','16','22','28','34','40','46','52','58')");}register_deactivation_hook(__F